mapper.select:
SELECT A.*, B.nm AS writernm, B.profileimg
FROM t_board_cmt A
LEFT JOIN t_user B
ON A.iuser = B.iuser
WHERE A.iboard = ${iboard}
ORDER BY A.icmt;

mapper.update:
UPDATE t_board_cmt
SET ctnt = #{ctnt}
WHERE icmt = ${icmt}
AND iuser = ${iuser}

mapper.delete:
DELETE FROM t_board_cmt
WHERE icmt = ${icmt}
AND iuser = ${iuser}
